mapper.select:
select sum(amount)
from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select count(id)
from user


        and create_time > #{begin}


        and create_time < #{end}

select count(id)
from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select od.name,sum(od.number) number from order_detail od,orders o where od.order_id = o.id and o.status =5
and o.order_time > #{begin}
and o.order_time < #{end}
group by od.name order by number desc limit 0,10
